import MoviesCardList from "../MoviesCardList/MoviesCardList";
import React, { useState, useEffect, useContext } from 'react';

import moviesApi from "../../utils/MoviesApi";

function Movies(props) {

    const [movies, setMovies] = useState([])

    // useEffect(() => {
