import React, { useState } from 'react';

function SearchForm({ onSearch, onFilter }) {
    const [searchTerm, setSearchTerm] = useState('');
    const [isShortMovies, setIsShortMovies] = useState(false);

    const handleSearch = (e) => {
        e.preventDefault();
        onSearch(searchTerm);
    };

    const handleFilterChange = () => {
        setIsShortMovies(!isShortMovies);
        onFilter(!isShortMovies);
    };

    return (
        <div className="searchForm">
            <form className="searchForm__form" onSubmit={handleSearch}>
                <input
                    className="searchForm__input"
                    name="searchForm"
                    placeholder="Фильм"
                    type="search"
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                />
                <button className="searchForm__submit" type="submit">
                    Найти
                </button>
            </form>
            <div className="movies__switch-container">
                <input
                    className="movies__switch"
                    id="switch"
                    type="checkbox"
                    checked={isShortMovies}
                    onChange={handleFilterChange}
                />
                <label className="movies__switch-active" htmlFor="switch"></label>
                <label className="movies__switch-label">Короткометражки</label>
            </div>
        </div>
    );
}

export default SearchForm;
