import useForm from "../../hooks/useForm";
import React, { useState, useEffect } from 'react';

function SearchForm({ findMovie }) {
    const { values, handleChange, setValues } = useForm({ searchForm: '' });
    const [isShortMovies, setIsShortMovies] = useState(false); // Локальное состояние для чекбокса

    let searching = values.searchForm;

    function handleSubmit(e) {
        e.preventDefault();
        if (values.searchForm === "") {
            console.log("где фильм?");
        } else {
            findMovie(searching, isShortMovies); // Передаем isShortMovies в функцию поиска
        }
    }

    function handleCheckboxChange() {
        setIsShortMovies(!isShortMovies); // Инвертируем состояние чекбокса
        findMovie(searching, !isShortMovies); // Передаем инвертированное состояние в функцию поиска
    }

    return (
        <div className="searchForm">
            <form className="searchForm__form" action="" method="get" onSubmit={handleSubmit}>
                <input
                    className="searchForm__input"
                    name="searchForm"
                    placeholder="Фильм"
                    type="search"
                    onChange={handleChange}
                    value={values.searchForm}
                />
                <button className="searchForm__submit" type="submit">
                    Найти
                </button>
            </form>
            <div className="movies__switch-container">
                <input
                    className="movies__switch"
                    id="switch"
                    type="checkbox"
                    checked={isShortMovies}
                    onChange={handleCheckboxChange}
                />
                <label className="movies__switch-active" for="switch"></label>
                <label className="movies__switch-lable">Короткометражки</label>
            </div>
        </div>
    );
}

export default SearchForm;
