import NavBar from '../NavBar/NavBar';
import signDataLogo from '../../images/header__logo.svg';
import { Link } from "react-router-dom";
import useForm from "../../hooks/useForm";
import useFormValidation from "../../hooks/useFormValidation";

function Register({ onRegister }) {

    const { values, handleChange, setValues } = useForm({ name: '', email: '', password: '' });
    const { handleSubmit, errors, isSubmitting } = useFormValidation(values, validate); // Подставьте свои правила валидации

    function validate(values) {
        const errors = {};
        // Ваши правила валидации здесь
        if (!values.name) {
            errors.name = 'Имя обязательно';
        }
        if (!values.email) {
            errors.email = 'Email обязателен';
        }
        // Другие правила валидации
        return errors;
    }
    

    const SubmitForm = (evt) => {
        handleSubmit(evt)
        onRegister(values, setValues)
    }

    return (
        <main className="sign-data">
            <Link className="sign-data__logo-link" to={'/'}>
                <img src={signDataLogo} className="sign-data__logo" alt="Логотип" />
            </Link>
            <h1 className="sign-data__title">Добро пожаловать!</h1>
            <form onSubmit={SubmitForm} className="sign-data__form">
                <label className="sign-data__label">Имя</label>
                <input
                    onChange={handleChange}
                    name="name"
                    placeholder="Имя"
                    minLength={2}
                    maxLength={30}
                    required
                    className="sign-data__input"
                />
                {errors.name && <span className="sign-data__input-error">{errors.name}</span>}

                <label className="sign-data__label">E-mail</label>
                <input
                    onChange={handleChange}
                    name="email"
                    placeholder="E-mail"
                    minLength={2}
                    maxLength={30}
                    required
                    className="sign-data__input"
                    type="email"
                />
                {errors.email && <span className="sign-data__input-error">{errors.email}</span>}

                <label className="sign-data__label">Пароль</label>
                <input
                    onChange={handleChange}
                    name="password"
                    placeholder="Пароль"
                    minLength={2}
                    maxLength={30}
                    required
                    className="sign-data__input"
                    type="password"
                />
                {errors.password && <span className="sign-data__input-error">{errors.password}</span>}

                <button className="sign-data__submit" type="submit" disabled={isSubmitting}>
                    Зарегистрироваться
                </button>
            </form>
            <p className="sign-data__text-under-submit">
                Уже зарегистрированы? <NavBar name={"Войти"} path={"sign-in"} className={"sign-data__link-under-submit"} />
            </p>
        </main>
    );
}

export default Register;
