import React, { useState, useEffect } from 'react';
import { mainApi } from "../../utils/MainApi";
import SearchForm from "../SearchForm/SearchForm";
import MoviesCardList from "../MoviesCardList/MoviesCardList";


function Movies({ openPopupError }) {
    const [movies, setMovies] = useState([]);
    const [allMovies, setAllMovies] = useState([]);
    const [isShortMovies, setIsShortMovies] = useState(false)
    const [filteredMovies, setFilteredMovies] = useState([]);

    useEffect(() => {
        mainApi.getMovies()
            .then((data) => {
                setMovies(data)
            })
            .catch((error) => {
                console.log(`${error}`);
                openPopupError(true)
            })
    }, []);

    function findMovie(filter, isShortMovies) {
        let filteredMovies = movies.filter(({ nameRU }) => nameRU.toLowerCase().includes(filter.toLowerCase()));
        console.log(filteredMovies)
        if (isShortMovies === true) {
            filteredMovies = filteredMovies.filter(({ duration }) => duration <= 40);
        }
        setAllMovies(filteredMovies);
        setMovies(filteredMovies);
        if (isShortMovies) {
            setFilteredMovies(filteredMovies);
        } else {
            setFilteredMovies(allMovies);
        }
    }

    function updateMoviesList (movieCard) {
        setMovies((prevMovies) => prevMovies.filter((movie) => movie._id !== movieCard._id));
    }

    function handleCheckboxChange() {
        setIsShortMovies(!isShortMovies);
        if (!isShortMovies) {
            setFilteredMovies(allMovies.filter(({ duration }) => duration <= 40));
        } else {
            setFilteredMovies(allMovies);
        }
    }

    return (
        <main className="movies">
            <SearchForm findMovie={findMovie} handleCheckboxChange={handleCheckboxChange}/>
            <MoviesCardList movies={movies} updateMoviesList={updateMoviesList}/>
        </main>
    );
}

export default Movies;
