import { useState } from "react";
import NavBar from '../NavBar/NavBar';

function Profile() {
    const [profileChange, setProfileChange] = useState(false);
    const [name, setName] = useState('Виталий');
    const [email, setEmail] = useState('pochta@yandex.ru');

    function changeProfile() {
        setProfileChange(true);
    }

    function handleSubmit(e) {
        e.preventDefault();
        // Здесь можно добавить логику отправки данных на сервер, если это необходимо
        setProfileChange(false);
    }

    return (
        <main className="profile">
            <h1 className="profile__title">Привет, Виталий!</h1>
            <form onSubmit={handleSubmit}>
                <div className="profile__info">
                    <h2 className="profile__info-type">Имя</h2>
                    {profileChange ? 
                        <input 
                            className="profile__info-item profile__info-name" 
                            placeholder="Имя"
                            value={name}
                            onChange={(e) => setName(e.target.value)}
                        />
                        : 
                        <p className="profile__info-item profile__info-name">{name}</p>
                    }
                </div>
                <div className="profile__info">
                    <h3 className="profile__info-type">E-mail</h3>
                    {profileChange ? 
                        <input 
                            className="profile__info-item profile__info-email" 
                            placeholder="E-mail"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                        />
                        : 
                        <p className="profile__info-item profile__info-email">{email}</p>
                    }
                </div>
                {profileChange ? (
                    <button className="profile__button" type="submit">Сохранить</button>
                ) : (
                    <button className="profile__button" onClick={changeProfile}>Редактировать</button>
                )}
            </form>
            <NavBar name={"Выйти из аккаунта"} path={""} className={"profile__link"} />
        </main>
    );
}

export default Profile;
