import React, { useState, useEffect, useContext } from 'react';
import { moviesApi } from "../../utils/MoviesApi";
import SearchForm from "../SearchForm/SearchForm";
import MoviesCardList from "../MoviesCardList/MoviesCardList";

function Movies() {
    const [movies, setMovies] = useState([]);
    const [visibleMovies, setVisibleMovies] = useState([]);
    const [maxVisibleMovies, setMaxVisibleMovies] = useState(16);
    const [searched, setSearched] = useState(false);

    let cardsPerRow = 4; // Количество карточек в ряду
    let loadMoreCount = 4; // Количество карточек для загрузки при нажатии на кнопку "Еще"

    useEffect(() => {
        findMovie("", false);
        updateScreenLayout(); // Определение количества карточек на экране при загрузке страницы
        window.addEventListener('resize', updateScreenLayout); // Обработчик изменения размера окна
    }, []);

    function findMovie(filter, isShortMovies) {
        moviesApi.getMovies()
            .then((data) => {
                let filteredMovies = data.filter(({ nameRU }) => nameRU.toLowerCase().includes(filter.toLowerCase()));
                if (isShortMovies === true) {
                    filteredMovies = filteredMovies.filter(({ duration }) => duration <= 30);
                }
                setMovies(filteredMovies);
                setMaxVisibleMovies(cardsPerRow);
                setSearched(true);
            })
            .catch((error) => {
                console.log(`${error}`);
            });
    }

    function loadMoreMovies() {
        setMaxVisibleMovies(prevMaxVisibleMovies => prevMaxVisibleMovies + loadMoreCount);
    }

    function updateScreenLayout() {
        const screenWidth = window.innerWidth;

        if (screenWidth <= 480) {
            cardsPerRow = 1;
            loadMoreCount = 2;
        } else if (screenWidth <= 768) {
            cardsPerRow = 4;
            loadMoreCount = 4;
        } else {
            cardsPerRow = 4;
            loadMoreCount = 4;
        }

        // Ограничиваем maxVisibleMovies, чтобы не превысить количество карточек в массиве
        if (maxVisibleMovies > movies.length) {
            setMaxVisibleMovies(movies.length);
        } else {
            setMaxVisibleMovies(maxVisibleMovies);
        }
    }

    useEffect(() => {
        if (searched) {
            updateVisibleMovies();
        }
    }, [movies, maxVisibleMovies, searched]);

    function updateVisibleMovies() {
        const moviesToShow = movies.slice(0, maxVisibleMovies);
        setVisibleMovies(moviesToShow);
    }

    return (
        <main className="movies">
            <SearchForm findMovie={findMovie}/>
            {searched && <MoviesCardList movies={visibleMovies} />}
            {searched && visibleMovies.length < movies.length && (
                <button className="movies__moreMovies-btn" onClick={loadMoreMovies}>Еще</button>
            )}
        </main>
    );
}

export default Movies;
