import { useState } from "react";
import NavBar from '../NavBar/NavBar';
import useForm from "../../hooks/useForm";

function Profile() {

    const [profileChange, setProfileChange] = useState(false)

    const { values, handleChange, setValues } = useForm({});

    function changeProfile() {
        setProfileChange(true)
    }

    function handleSubmit(e) {
        e.preventDefault();

    }


    return (
        <main className="profile">
            <h1 className="profile__title">Привет, Виталий!</h1>
            <form className={profileChange ? "profile__form profile__form_margin" : "profile__form"} onSubmit={handleSubmit}>
                <div className="profile__info">
                    <h2 className="profile__info-type">Имя</h2>
                    {profileChange ? <input onChange={handleChange}
                        className="profile__info-item profile__info-name profile__input" placeholder="Имя"></input>
                        : <p className="profile__info-item profile__info-name">Виталий</p>}
                </div>
                <div className="profile__info">
                    <h3 className="profile__info-type">E-mail</h3>
                    {profileChange ? <input onChange={handleChange}
                        className="profile__info-item profile__info-name profile__input" placeholder="E-mail"></input>
                        : <p className="profile__info-item profile__info-email">pochta@yandex.ru</p>}
                </div>
                {profileChange ? <button className="profile__form-submit">Сохранить</button> : ""}
            </form>
            {profileChange ? "" : <div className="profile__under-submit-container">
                {profileChange ? "" : <button className="profile__button" onClick={changeProfile}>Редактировать</button>}
                <NavBar name={"Выйти из аккаунта"} path={""} className={"profile__link"} />
            </div>}
        </main>
    );
}

export default Profile;
