import NavBar from '../NavBar/NavBar';
import { Link } from "react-router-dom";
import useForm from "../../hooks/useForm";
import { useState, useEffect } from "react";

function Profile(props) {
    const [profileChange, setProfileChange] = useState(false);
    const [formValid, setFormValid] = useState(false);
    const [errors, setErrors] = useState({});
    const [isSubmitting, setIsSubmitting] = useState(false);

    const { values, handleChange, setValues } = useForm({});
    const profileData = React.useContext(CurrentUserContext);

    useEffect(() => {
        if (profileChange) {
            validateForm();
        }
    }, [values, profileChange]);

    function validateForm() {
        const isFormValid = Object.values(values).every(value => value && value.length >= 2);
        setFormValid(isFormValid);

        if (values.name === profileData.name && values.email === profileData.email) {
            setErrors({ noChanges: 'Данные не изменены' });
        } else {
            setErrors({ noChanges: '' });
        }
    }

    const handleBlur = (e) => {
        const { name, value } = e.target;
        if (value.length < 2) {
            e.target.setCustomValidity('Это поле должно содержать минимум 2 символа');
        } else {
            e.target.setCustomValidity('');
        }
        validateForm();
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        if (profileData.email === values.email) {
            props.onUpdateUser({
                name: values.name,
                email: values.email,
            });
            setIsSubmitting(true);
        } else {
            // Simulate the email uniqueness check, you should replace this with a real check
            setTimeout(() => {
                const isEmailUnique = checkEmailUniqueness(values.email); // Implement this function
                if (isEmailUnique) {
                    props.onUpdateUser({
                        name: values.name,
                        email: values.email,
                    });
                    setIsSubmitting(true);
                } else {
                    setErrors({ emailExists: 'Пользователь с такой почтой уже существует' });
                }
            }, 1000); // Simulated delay
        }
    }

    function checkEmailUniqueness(email) {
        // Implement a function to check email uniqueness here
        return true; // Replace this with the actual check
    }

    return (
        <main className="profile">
            <h1 className="profile__title">Привет, {profileData.name || 'Виталий'}!</h1>
            <form className={profileChange ? "profile__form profile__form_margin" : "profile__form"} onSubmit={handleSubmit}>
                {/* ...other code... */}
                <span className="sign-data__input-error profile-data__input-error">
                    {errors.noChanges && <p>{errors.noChanges}</p>}
                    {errors.emailExists && <p>{errors.emailExists}</p>}
                    {isSubmitting && !errors.emailExists && <p>Произошла ошибка при обновлении профиля</p>}
                </span>
                {/* ...other code... */}
            </form>
            {/* ...other code... */}
        </main>
    );
}

export default Profile;
