import React, { useState, useEffect } from 'react';

function SearchForm({ findMovie }) {
    const [isShortMovies, setIsShortMovies] = useState(false);

    function handleSubmit(e) {
        e.preventDefault();
        // Вызывайте функцию findMovie с флагом isShortMovies
        findMovie(isShortMovies);
    }

    function handleCheckboxChange() {
        setIsShortMovies(!isShortMovies);
    }

    return (
        <div className="searchForm">
            <form className="searchForm__form" action="" method="get" onSubmit={handleSubmit}>
                {/* Удалите input для поиска, если не нужно выполнять поиск */}
                <button className="searchForm__submit" type="submit">Применить фильтр</button>
            </form>
            <div className="movies__switch-container">
                <input className="movies__switch" id="switch" type="checkbox" checked={isShortMovies} onChange={handleCheckboxChange} />
                <label className="movies__switch-active" for="switch"></label>
                <label className="movies__switch-label">Короткометражки</label>
            </div>
        </div>
    );
}

export default SearchForm;
