import React, { useState, useEffect } from 'react';
import { moviesApi } from '../../utils/MoviesApi';
import SearchForm from '../SearchForm/SearchForm';
import MoviesCardList from '../MoviesCardList/MoviesCardList';

function Movies() {
    const [allMovies, setAllMovies] = useState([]);
    const [filteredMovies, setFilteredMovies] = useState([]);
    const [visibleMovies, setVisibleMovies] = useState([]);
    const [maxVisibleMovies, setMaxVisibleMovies] = useState(16);
    const [searched, setSearched] = useState(false);
    const [isShortMovies, setIsShortMovies] = useState(false);
    const [searchQuery, setSearchQuery] = useState('');

    const screenWidth = window.innerWidth;

    useEffect(() => {
        const usersMovies = JSON.parse(localStorage.getItem('oldMovies'));
        if (usersMovies) {
            setAllMovies(usersMovies);
            setFilteredMovies(usersMovies);
            setSearched(true);
            updateVisibleMovies();
        }
    }, []);

    useEffect(() => {
        if (searched) {
            updateVisibleMovies();
        }
    }, [filteredMovies, maxVisibleMovies, searched]);

    function findMoviesWithQuery(query, isShort) {
        setSearchQuery(query);

        const filteredMovies = allMovies.filter(({ nameRU }) => nameRU.toLowerCase().includes(query.toLowerCase()));
        if (isShort) {
            setFilteredMovies(filteredMovies.filter(({ duration }) => duration <= 40));
        } else {
            setFilteredMovies(filteredMovies);
        }

        setSearched(true);
    }

    function handleCheckboxChange() {
        setIsShortMovies(!isShortMovies);
        if (searchQuery) {
            findMoviesWithQuery(searchQuery, isShortMovies);
        }
    }

    function loadMoreMovies() {
        const cardsToAdd = screenWidth <= 1150 ? (screenWidth <= 480 ? 2 : 3) : 4;
        setMaxVisibleMovies(currentMaxVisibleMovies => currentMaxVisibleMovies + cardsToAdd);
    }

    function updateVisibleMovies() {
        const moviesToShow = filteredMovies.slice(0, maxVisibleMovies);
        setVisibleMovies(moviesToShow);
    }

    return (
        <main className="movies">
            <SearchForm findMovies={findMoviesWithQuery} isShortMovies={isShortMovies} handleCheckboxChange={handleCheckboxChange} />
            {searched && filteredMovies.length === 0 ? (
                <p>Фильм не найден</p>
            ) : (
                <>
                    {searched && <MoviesCardList movies={visibleMovies} />}
                    {searched && visibleMovies.length < filteredMovies.length && (
                        <button className="movies__moreMovies-btn" onClick={loadMoreMovies}>
                            Еще
                        </button>
                    )}
                </>
            )}
        </main>
    );
}

export default Movies;
