import React, { useState, useEffect } from 'react';
import { mainApi } from "../../utils/MainApi";
import SearchForm from "../SearchForm/SearchForm";
import MoviesCardList from "../MoviesCardList/MoviesCardList";

function Movies({ openPopupError }) {
    const [originalMovies, setOriginalMovies] = useState([]);
    const [movies, setMovies] = useState([]);

    useEffect(() => {
        mainApi.getMovies()
            .then((data) => {
                setOriginalMovies(data);
                setMovies(data);
            })
            .catch((error) => {
                console.log(`${error}`);
                openPopupError(true);
            });

    }, [originalMovies]);

    function findMovie(filter, isShortMovies) {
        let filteredMovies = originalMovies.filter(({ nameRU }) => nameRU.toLowerCase().includes(filter.toLowerCase()));
        console.log(filteredMovies);
        if (isShortMovies === true) {
            filteredMovies = filteredMovies.filter(({ duration }) => duration <= 40);
        }
        setMovies(filteredMovies);
    }

    return (
        <main className="movies">
            <SearchForm findMovie={findMovie} />
            <MoviesCardList movies={movies} />
        </main>
    );
}

export default Movies;
