import { useState } from "react";
import NavBar from '../NavBar/NavBar';
import useForm from "../../hooks/useForm";

function Profile({ signOut }) {

    const [profileChange, setProfileChange] = useState(false)
    const [name, setName] = useState("")
    const [email, setEmail] = useState("")

    const { values, handleChange, setValues } = useForm({});

    function changeProfile() {
        setProfileChange(true)
    }

    function handleSubmit(e) {
        e.preventDefault();
        setName(values.name)
        setEmail(values.email)
        setProfileChange(false)
    }

    function exit() {
        signOut()
    }


    return (
        <main className="profile">
            <h1 className="profile__title">Привет, {name || 'Виталий'}!</h1>
            <form className={profileChange ? "profile__form profile__form_margin" : "profile__form"} onSubmit={handleSubmit}>
                <div className="profile__info">
                    <h2 className="profile__info-type">Имя</h2>
                    {profileChange ? <input onChange={handleChange} name="name" maxLength={30} minLength={2} required
                        className="profile__info-item profile__info-name profile__input" placeholder={name || "Имя"}></input>
                        : <p className="profile__info-item profile__info-name">{name || 'Виталий'}</p>}
                </div>
                <div className="profile__info">
                    <h3 className="profile__info-type">E-mail</h3>
                    {profileChange ? <input onChange={handleChange} name="email" maxLength={30} minLength={2} required
                        className="profile__info-item profile__info-name profile__input" placeholder={name || "E-mail"}></input>
                        : <p className="profile__info-item profile__info-email">{email || "pochta@yandex.ru"}</p>}
                </div>
                {profileChange ? <button className="profile__form-submit">Сохранить</button> : ""}
            </form>
            {profileChange ? "" : <div className="profile__under-submit-container">
                {profileChange ? "" : <button className="profile__button" onClick={changeProfile}>Редактировать</button>}
                <NavBar name={"Выйти из аккаунта"} path={""} className={"profile__link"} onClick={exit} />
            </div>}
        </main>
    );
}

export default Profile;
