import SearchForm from "../SearchForm/SearchForm";
import MoviesCardList from "../MoviesCardList/MoviesCardList";
import React, { useState, useEffect, useContext } from 'react';

import { moviesApi } from "../../utils/MoviesApi";

function Movies() {

    const [movies, setMovies] = useState([]);
    const [isShortMovies, setIsShortMovies] = useState(false);

    function findMovie(filter) {
        moviesApi.getMovies()
            .then((data) => {
                let filteredMovies = data.filter(({ nameRU }) => nameRU.toLowerCase().includes(filter.toLowerCase()));
                if (isShortMovies) {
                    filteredMovies = filteredMovies.filter((movie) => movie.duration <= 30);
                }
                setMovies(filteredMovies)
            })
            .catch((error) => {
                console.log(`${error}`);
            })
    }

    function handleCheckboxChange() {
        setIsShortMovies(!isShortMovies);
        findMovie();
        console.log(isShortMovies)
    }

    return (
        <main className="movies">
            <SearchForm findMovie={findMovie} />
            <div className="movies__switch-container">
                <input className="movies__switch" id="switch" type="checkbox" checked={isShortMovies}
                    onChange={handleCheckboxChange} />
                <label className="movies__switch-active" for="switch"></label>
                <label className="movies__switch-lable">Короткометражки</label>
            </div>
            <MoviesCardList movies={movies} />
            <button className="movies__moreMovies-btn">Еще</button>
        </main>
    );
}

export default Movies;
