import React, { useState, useEffect, useContext } from 'react';
import { moviesApi } from "../../utils/MoviesApi";
import SearchForm from "../SearchForm/SearchForm";
import MoviesCardList from "../MoviesCardList/MoviesCardList";

function Movies() {
    const [movies, setMovies] = useState([]);
    const [visibleMovies, setVisibleMovies] = useState([]);
    const [maxVisibleMovies, setMaxVisibleMovies] = useState(16);
    const [searched, setSearched] = useState(false);
    const [allMovies, setAllMovies] = useState([]); // Состояние для всех фильмов
    const [filteredMovies, setFilteredMovies] = useState([]); // Состояние для отфильтрованных фильмов

    const screenWidth = window.innerWidth;

    useEffect(() => {
        const usersMovies = JSON.parse(localStorage.getItem('oldMovies'));
        if (usersMovies) {
            setMovies(usersMovies);
            setSearched(true);
            updateVisibleMovies();
        }
    }, []);

    function findMovie(filter, isShortMovies) {
        moviesApi.getMovies()
            .then((data) => {
                let filteredMovies = data.filter(({ nameRU }) => nameRU.toLowerCase().includes(filter.toLowerCase()));
                if (isShortMovies === true) {
                    filteredMovies = filteredMovies.filter(({ duration }) => duration <= 40);
                }
                setAllMovies(filteredMovies);
                localStorage.setItem('oldMovies', JSON.stringify(filteredMovies));
                if (isShortMovies) {
                    // Если чекбокс активен, отфильтровать все фильмы
                    setFilteredMovies(filteredMovies);
                } else {
                    // Если чекбокс не активен, показать все фильмы
                    setFilteredMovies(allMovies);
                }

                if (screenWidth <= 1150) {
                    setMaxVisibleMovies(15);
                } else {
                    setMaxVisibleMovies(16);
                }
                setSearched(true);
            })
            .catch((error) => {
                console.log(`${error}`);
            });
    }

    function handleCheckboxChange(setIsShortMovies, isShortMovies) {
        setIsShortMovies(!isShortMovies);
        if (isShortMovies) {
            setFilteredMovies(allMovies.filter(({ duration }) => duration <= 40));
        } else {
            setFilteredMovies(allMovies);
        }
    }

    function loadMoreMovies() {
        let cardsToAdd = 4;
        if (screenWidth <= 1150) {
            cardsToAdd = 3;
        }
        if (screenWidth <= 480) {
            cardsToAdd = 2;
        }
        setMaxVisibleMovies(currentMaxVisibleMovies => currentMaxVisibleMovies + cardsToAdd);
    }

    useEffect(() => {
        if (searched) {
            updateVisibleMovies();
        }
    }, [movies, maxVisibleMovies, searched]);

    function updateVisibleMovies() {
        const moviesToShow = movies.slice(0, maxVisibleMovies);
        setVisibleMovies(moviesToShow);
    }

    return (
        <main className="movies">
            <SearchForm findMovie={findMovie} onChange={handleCheckboxChange}/>
            {searched && movies.length === 0 ? (
                <p>Фильм не найден</p>
            ) : (
                <>
                    {searched && <MoviesCardList movies={visibleMovies} />}
                    {searched && visibleMovies.length < movies.length && (
                        <button className="movies__moreMovies-btn" onClick={loadMoreMovies}>Еще</button>
                    )}
                </>
            )}
        </main>
    );
}

export default Movies;
