function Profile(props) {
    const [profileChange, setProfileChange] = useState(false);
    const profileData = React.useContext(CurrentUserContext);
    const { values, handleChange, setValues } = useForm({});
    
    // Определяем, были ли изменения
    const hasChanges = profileChange && (values.name !== profileData.name || values.email !== profileData.email);

    function changeProfile() {
        setProfileChange(true);
        setValues({ name: profileData.name, email: profileData.email });
    }

    function handleSubmit(e) {
        e.preventDefault();
        props.onUpdateUser({
            name: values.name,
            email: values.email,
        });
        setProfileChange(false);
    }

    return (
        <main className="profile">
            <h1 className="profile__title">Привет, {profileData.name || 'Виталий'}!</h1>
            <form className={profileChange ? "profile__form profile__form_margin" : "profile__form"} onSubmit={handleSubmit}>
                <div className="profile__info">
                    <h2 className="profile__info-type">Имя</h2>
                    {profileChange ? (
                        <input
                            onChange={handleChange}
                            name="name"
                            maxLength={30}
                            minLength={2}
                            required
                            className="profile__info-item profile__info-name profile__input"
                            placeholder={"Имя"}
                            value={values.name || ""}
                        />
                    ) : (
                        <p className="profile__info-item profile__info-name">
                            {profileData.name || 'Виталий'}
                        </p>
                    )}
                </div>
                <div className="profile__info">
                    <h3 className="profile__info-type">E-mail</h3>
                    {profileChange ? (
                        <input
                            onChange={handleChange}
                            value={values.email || ""}
                            name="email"
                            maxLength={30}
                            minLength={2}
                            required
                            className="profile__info-item profile__info-name profile__input"
                            placeholder={"E-mail"}
                        />
                    ) : (
                        <p className="profile__info-item profile__info-email">
                            {profileData.email || "pochta@yandex.ru"}
                        </p>
                    )}
                </div>
                {profileChange && (
                    <button
                        className="profile__form-submit"
                        disabled={!hasChanges}
                    >
                        Сохранить
                    </button>
                )}
            </form>
            {!profileChange && (
                <div className="profile__under-submit-container">
                    <button className="profile__button" onClick={changeProfile}>
                        Редактировать
                    </button>
                    <NavBar name={"Выйти из аккаунта"} path={""} className={"profile__link"} signOut={props.signOut} />
                </div>
            )}
        </main>
    );
}
