import React, { useState, useEffect } from 'react';
import useForm from '../../hooks/useForm';

function SearchForm({ findMovies, isShortMovies, handleCheckboxChange }) {
  const { values, handleChange, setValues } = useForm({ searchForm: '' });

  useEffect(() => {
    const SearchFormValueData = localStorage.getItem('searchFormValue');
    if (SearchFormValueData) {
      setValues({ searchForm: SearchFormValueData });
    }
  }, []);

  function handleSubmit(e) {
    e.preventDefault();
    const query = values.searchForm;
    if (query === '') {
      console.log('Введите запрос');
    } else {
      findMovies(query, isShortMovies);
      localStorage.setItem('searchFormValue', query);
    }
  }

  return (
    <div className="searchForm">
      <form className="searchForm__form" action="" method="get" onSubmit={handleSubmit}>
        <input
          className="searchForm__input"
          name="searchForm"
          placeholder="Фильм"
          type="search"
          onChange={handleChange}
          value={values.searchForm}
        />
        <button className="searchForm__submit" type="submit">
          Найти
        </button>
      </form>
      <div className="movies__switch-container">
        <input className="movies__switch" id="switch" type="checkbox" checked={isShortMovies} onChange={handleCheckboxChange} />
        <label className="movies__switch-active" htmlFor="switch"></label>
        <label className="movies__switch-label">Короткометражки</label>
      </div>
    </div>
  );
}

export default SearchForm;
