import React, { useState, useEffect } from 'react';
import useForm from "../../hooks/useForm";

function SearchForm({ findMovie, isShortMovies, onCheckboxChange }) {
    const { values, handleChange, setValues } = useForm({ searchForm: '' });

    useEffect(() => {
        const SearchFormValueData = localStorage.getItem('searchFormValue');
        if (SearchFormValueData) {
            setValues({ searchForm: SearchFormValueData });
        }
    }, []);

    function handleSubmit(e) {
        e.preventDefault();
        if (values.searchForm === '') {
            console.log('Введите запрос');
        } else {
            findMovie(values.searchForm, isShortMovies);
            localStorage.setItem('searchFormValue', values.searchForm);
        }
    }

    const handleCheckboxChange = () => {
        // Обновляем состояние чекбокса в родительском компоненте
        onCheckboxChange(!isShortMovies);

        // Сохраняем состояние чекбокса в localStorage
        localStorage.setItem('isShortMovies', !isShortMovies);
    };


    return (
        <div className="searchForm">
            <form className="searchForm__form" action="" method="get" onSubmit={handleSubmit}>
                <input
                    className="movies__switch"
                    id="switch"
                    type="checkbox"
                    checked={isShortMovies}
                    onChange={handleCheckboxChange}
                />
                <button className="searchForm__submit" type="submit">
                    Найти
                </button>
            </form>
            <div className="movies__switch-container">
                <input
                    className="movies__switch"
                    id="switch"
                    type="checkbox"
                    checked={isShortMovies}
                    onChange={handleCheckboxChange}
                />
                <label className="movies__switch-active" htmlFor="switch"></label>
                <label className="movies__switch-lable">Короткометражки</label>
            </div>
        </div>
    );
}

export default SearchForm;
